"use client"

import { createContext, useContext, useState } from "react"

import { AppSidebar } from "@/registry/bellhop/blocks/sidebar-00/components/app-sidebar"
import {
  areasData,
  modulesData,
  productsData,
} from "@/registry/bellhop/blocks/sidebar-00/components/nav-data"
import { NavModules } from "@/registry/bellhop/blocks/sidebar-00/components/nav-modules"
import { NavProducts } from "@/registry/bellhop/blocks/sidebar-00/components/nav-products"
import {
  BellhopBreadcrumb as Breadcrumb,
  BellhopBreadcrumbItem as BreadcrumbItem,
  BellhopBreadcrumbLink as BreadcrumbLink,
  BellhopBreadcrumbList as BreadcrumbList,
  BellhopBreadcrumbPage as BreadcrumbPage,
  BellhopBreadcrumbSeparator as BreadcrumbSeparator,
} from "@/registry/bellhop/ui/breadcrumb"
import {
  BellhopSidebarInset as SidebarInset,
  BellhopSidebarProvider as SidebarProvider,
  BellhopSidebarTrigger as SidebarTrigger,
} from "@/registry/bellhop/ui/sidebar"

// Navigation Context
type NavigationContextType = {
  selectedProduct: string | null
  setSelectedProduct: (productId: string | null) => void
  selectedModule: string | null
  setSelectedModule: (moduleId: string | null) => void
  selectedArea: string | null
  setSelectedArea: (areaId: string | null) => void
  getCurrentProduct: () => any
  getCurrentModule: () => any
  getCurrentArea: () => any
}

const NavigationContext = createContext<NavigationContextType | undefined>(
  undefined
)

export function useNavigation() {
  const context = useContext(NavigationContext)
  if (!context) {
    throw new Error("useNavigation must be used within a NavigationProvider")
  }
  return context
}

function NavigationProvider({ children }: { children: React.ReactNode }) {
  const [selectedProduct, setSelectedProduct] = useState<string | null>("al")
  const [selectedModule, setSelectedModule] = useState<string | null>(
    "contacts"
  )
  const [selectedArea, setSelectedArea] = useState<string | null>(
    "all-contacts"
  )

  const getCurrentProduct = () => {
    return selectedProduct
      ? productsData[selectedProduct as keyof typeof productsData]
      : null
  }

  const getCurrentModule = () => {
    if (!selectedProduct || !selectedModule) return null
    const modules =
      modulesData[selectedProduct as keyof typeof modulesData] || []
    return modules.find((m) => m.id === selectedModule)
  }

  const getCurrentArea = () => {
    if (!selectedModule || !selectedArea) return null
    const areas = areasData[selectedModule as keyof typeof areasData] || []
    return areas.find((a) => a.id === selectedArea)
  }

  const handleSetSelectedProduct = (productId: string | null) => {
    setSelectedProduct(productId)
    if (productId) {
      // Auto-select first module for the product
      const modules = modulesData[productId as keyof typeof modulesData] || []
      const firstModule = modules[0]
      if (firstModule) {
        setSelectedModule(firstModule.id)
        // Auto-select first area for the module
        const areas = areasData[firstModule.id as keyof typeof areasData] || []
        const firstArea = areas[0]
        if (firstArea) {
          setSelectedArea(firstArea.id)
        }
      }
    }
  }

  const handleSetSelectedModule = (moduleId: string | null) => {
    setSelectedModule(moduleId)
    if (moduleId) {
      // Auto-select first area for the module
      const areas = areasData[moduleId as keyof typeof areasData] || []
      const firstArea = areas[0]
      setSelectedArea(firstArea ? firstArea.id : null)
    }
  }

  const value = {
    selectedProduct,
    setSelectedProduct: handleSetSelectedProduct,
    selectedModule,
    setSelectedModule: handleSetSelectedModule,
    selectedArea,
    setSelectedArea,
    getCurrentProduct,
    getCurrentModule,
    getCurrentArea,
  }

  return (
    <NavigationContext.Provider value={value}>
      {children}
    </NavigationContext.Provider>
  )
}

export default function Page() {
  return (
    <NavigationProvider>
      <PageContent />
    </NavigationProvider>
  )
}

function PageContent() {
  const {
    getCurrentProduct,
    getCurrentModule,
    getCurrentArea,
    setSelectedProduct,
    setSelectedModule,
  } = useNavigation()

  const currentProduct = getCurrentProduct()
  const currentModule = getCurrentModule()
  const currentArea = getCurrentArea()

  return (
    <div className="flex h-screen">
      {/* Level 1: Products Menu (Overlay/Floating) */}
      <NavProducts />

      {/* Level 2: Modules Menu (Fixed Icon Panel) */}
      <NavModules />

      {/* Level 3: Areas Menu (Collapsible Sidebar) + Main Content */}
      <SidebarProvider
        style={
          {
            "--sidebar-width": "260px",
          } as React.CSSProperties
        }
      >
        <AppSidebar className="ml-17 py-1" />

        {/* Main Content with Dynamic Updates */}
        <SidebarInset>
          <header className="bg-background sticky top-0 flex h-10 shrink-0 items-center gap-2 px-4 pt-4">
            <SidebarTrigger className="text-muted-foreground -ml-2 size-8" />
            <Breadcrumb className="ml-2">
              <BreadcrumbList>
                {currentModule && (
                  <>
                    <BreadcrumbItem>
                      {currentArea ? (
                        <BreadcrumbLink
                          href="#"
                          onClick={(e) => {
                            e.preventDefault()
                            // Navigate to module level (show first area or module overview)
                          }}
                        >
                          {currentModule.title}
                        </BreadcrumbLink>
                      ) : (
                        <BreadcrumbPage>{currentModule.title}</BreadcrumbPage>
                      )}
                    </BreadcrumbItem>
                  </>
                )}
                {currentArea && (
                  <>
                    <BreadcrumbSeparator />
                    <BreadcrumbItem>
                      <BreadcrumbPage>{currentArea.title}</BreadcrumbPage>
                    </BreadcrumbItem>
                  </>
                )}
              </BreadcrumbList>
            </Breadcrumb>
          </header>
          <div className="flex flex-1 flex-col gap-4 p-4">
            <h1 className="text-secondary-foreground text-xl font-medium">
              {currentArea?.title || "None"}
            </h1>
            <div className="bg-muted/80 flex min-h-[80vh] flex-1 items-center justify-center rounded-xl">
              <div className="space-y-4 text-center">
                <h2 className="text-xl font-medium">Content Changed!</h2>
                <div className="space-y-1 text-sm">
                  <p>
                    <strong>Product:</strong> {currentProduct?.name || "None"}
                  </p>
                  <p>
                    <strong>Module:</strong> {currentModule?.title || "None"}
                  </p>
                  <p>
                    <strong>Area:</strong> {currentArea?.title || "None"}
                  </p>
                </div>
                <p className="text-muted-foreground mt-4 text-xs">
                  Content updates when you select different items in the 3-level
                  navigation
                </p>
              </div>
            </div>
          </div>
        </SidebarInset>
      </SidebarProvider>
    </div>
  )
}

