"use client"

import * as React from "react"
import { Briefcase, Building2, Shield } from "lucide-react"

import {
  areasData,
  teamsData,
  userData,
} from "@/registry/bellhop/blocks/sidebar-00/components/nav-data"
import { NavMain } from "@/registry/bellhop/blocks/sidebar-00/components/nav-main"
import { NavUser } from "@/registry/bellhop/blocks/sidebar-00/components/nav-user"
import { TeamSwitcher } from "@/registry/bellhop/blocks/sidebar-00/components/team-switcher"
import { useNavigation } from "@/registry/bellhop/blocks/sidebar-00/page"
import {
  BellhopSidebar as Sidebar,
  BellhopSidebarContent as SidebarContent,
  BellhopSidebarFooter as SidebarFooter,
  BellhopSidebarHeader as SidebarHeader,
  BellhopSidebarRail as SidebarRail,
} from "@/registry/bellhop/ui/sidebar"

// Convert team data to include actual icon components
const teams = teamsData.map((team) => ({
  ...team,
  logo:
    team.logo === "Building2"
      ? Building2
      : team.logo === "Briefcase"
        ? Briefcase
        : Shield,
}))

export function AppSidebar({ ...props }: React.ComponentProps<typeof Sidebar>) {
  const { selectedModule, selectedArea, setSelectedArea, getCurrentModule } =
    useNavigation()

  // Get areas for current module
  const currentAreas = selectedModule
    ? areasData[selectedModule as keyof typeof areasData] || []
    : []

  const currentModule = getCurrentModule()

  return (
    <Sidebar collapsible="icon" {...props}>
      <SidebarHeader>
        <TeamSwitcher teams={teams} />
      </SidebarHeader>
      <SidebarContent>
        <NavMain
          items={currentAreas}
          selectedArea={selectedArea}
          onAreaSelect={setSelectedArea}
          groupLabel={currentModule?.title || "Areas"}
        />
      </SidebarContent>
      <SidebarFooter>
        <NavUser user={userData} />
      </SidebarFooter>
      <SidebarRail />
    </Sidebar>
  )
}

