"use client"

import * as React from "react"

import {
  ActablLogos,
  ActablLogosVariants,
} from "@/registry/bellhop/blocks/sidebar-00/components/actabl-logos"
import { GradientBar } from "@/registry/bellhop/blocks/sidebar-00/components/gradient-bar"
import { navProductsData } from "@/registry/bellhop/blocks/sidebar-00/components/nav-data"
import { useNavigation } from "@/registry/bellhop/blocks/sidebar-00/page"
import { BellhopButton as Button } from "@/registry/bellhop/ui/button"

export function NavProducts() {
  const [isOpen, setIsOpen] = React.useState(false)
  const [isHovering, setIsHovering] = React.useState(false)
  const { selectedProduct, setSelectedProduct } = useNavigation()

  const showSidebar = isOpen || isHovering

  const handleProductSelect = (productId: string) => {
    setSelectedProduct(productId)
    setIsOpen(false)
  }

  return (
    <>
      {/* GradientBar - Always visible trigger */}
      <div
        className="fixed top-0 left-0 z-50 h-full w-3 cursor-pointer"
        onMouseEnter={() => setIsHovering(true)}
        onMouseLeave={() => setIsHovering(false)}
        onClick={() => setIsOpen(!isOpen)}
      >
        <GradientBar />
      </div>

      {/* Products Menu Overlay */}
      {showSidebar && (
        <div
          className="z-40 flex-col items-center gap-4"
          onClick={() => setIsOpen(false)}
        >
          <div
            className="bg-background fixed top-0 left-0 ml-3 flex h-full w-[56px] flex-col items-center space-y-6 py-4 shadow-md"
            onClick={(e) => e.stopPropagation()}
            onMouseEnter={() => setIsHovering(true)}
            onMouseLeave={() => setIsHovering(false)}
          >
            {navProductsData.map((item) => (
              <Button
                key={item.name}
                variant={selectedProduct === item.id ? "default" : "ghost"}
                size="icon"
                onClick={() => handleProductSelect(item.id)}
                className="h-10 w-10 p-0"
                title={item.name}
              >
                <ActablLogos
                  variant={item.logo as keyof typeof ActablLogosVariants}
                />
              </Button>
            ))}
          </div>
        </div>
      )}
    </>
  )
}

