import { BellhopButton as Button } from "@/registry/bellhop/ui/button"
import {
  BellhopDropdownMenu as DropdownMenu,
  BellhopDropdownMenuContent as DropdownMenuContent,
  BellhopDropdownMenuItem as DropdownMenuItem,
  BellhopDropdownMenuLabel as DropdownMenuLabel,
  BellhopDropdownMenuSeparator as DropdownMenuSeparator,
  BellhopDropdownMenuShortcut as DropdownMenuShortcut,
  BellhopDropdownMenuTrigger as DropdownMenuTrigger,
} from "@/registry/bellhop/ui/dropdown-menu"
import {
  BellhopSidebarMenu as SidebarMenu,
  BellhopSidebarMenuButton as SidebarMenuButton,
  BellhopSidebarMenuItem as SidebarMenuItem,
  useBellhopSidebar as useSidebar,
} from "@/registry/bellhop/ui/sidebar"

export function TeamSwitcher({
  teams,
}: {
  teams: {
    name: string
    logo: React.ElementType
    plan: string
  }[]
}) {
  const { isMobile, open: isSidebarGloballyCollapsed } = useSidebar()
  const [activeTeam, setActiveTeam] = React.useState(teams[0])

  if (!activeTeam) {
    return null
  }

  return (
    <SidebarMenu>
      <SidebarMenuItem>
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button
              variant="ghost"
              className={`flex w-full px-1 transition-all delay-200 ease-in-out ${
                isSidebarGloballyCollapsed
                  ? "justify-beetween text-left text-sm"
                  : "justify-beetween aspect-square size-8"
              }`}
              aria-label={
                isSidebarGloballyCollapsed ? activeTeam.name : "Select team"
              }
            >
              {isSidebarGloballyCollapsed ? (
                <>
                  <div className="w-full gap-2">
                    <span className="justify-beetween max-w-[150px] truncate">
                      {activeTeam.name}
                    </span>
                  </div>
                  <ChevronsUpDown className="size-4 opacity-90" />
                </>
              ) : (
                <Building className="h-4 w-4 opacity-50" />
              )}
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent
            className="w-(--radix-dropdown-menu-trigger-width) min-w-56 rounded-md"
            align="start"
            side={isMobile ? "bottom" : "right"}
            sideOffset={4}
          >
            <DropdownMenuLabel className="text-muted-foreground text-xs">
              Sites
            </DropdownMenuLabel>
            {teams.map((team, index) => (
              <DropdownMenuItem
                key={team.name}
                onClick={() => setActiveTeam(team)}
                className="gap-2 p-2 text-sm"
              >
                {team.name}
                <DropdownMenuShortcut>⌘{index + 1}</DropdownMenuShortcut>
              </DropdownMenuItem>
            ))}
          </DropdownMenuContent>
        </DropdownMenu>
      </SidebarMenuItem>
    </SidebarMenu>
  )
}

