export function NavModules() {
  const { selectedProduct, selectedModule, setSelectedModule } = useNavigation()

  // Get modules for current product
  const currentModules = selectedProduct
    ? modulesData[selectedProduct as keyof typeof modulesData] || []
    : []

  // Don't render if no product selected
  if (!selectedProduct || currentModules.length === 0) {
    return (
      <div className="bg-background flex w-[59px] flex-col items-center border-r py-1">
        <div className="text-muted-foreground px-1 text-center text-xs">
          Select a product
        </div>
      </div>
    )
  }

  return (
    <div className="bg-background border-border ml-3 box-border w-[59px] border-r py-1">
      <div className="mx-auto flex flex-col items-center space-y-2">
        <SearchForm />
        {currentModules.map((module) => {
          const IconComponent =
            moduleIcons[module.id as keyof typeof moduleIcons]
          const isActive = selectedModule === module.id

          return (
            <Button
              key={module.id}
              variant={isActive ? "secondary" : "ghost"}
              size="icon"
              onClick={() => setSelectedModule(module.id)}
              className="size-9 p-0"
              title={module.title}
            >
              {IconComponent && <IconComponent size={16} className="size-4" />}
            </Button>
          )
        })}
      </div>
    </div>
  )
}

