"use client";

import { jsx as _jsx } from "react/jsx-runtime";
import React, { createContext, useContext } from "react";

import { X } from "lucide-react";
import { cva, VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";
import { Slot } from "@radix-ui/react-slot";

const BadgeContext = createContext<{ size: "sm" | "md" }>({ size: "sm" });

const badgeVariants = cva(
  "inline-flex items-center justify-center gap-1 font-medium transition-colors w-fit whitespace-nowrap shrink-0 inline-flex [&>svg]:size-3 [&>svg]:gap-1 [&>svg]:pointer-events-none focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 aria-invalid:ring-destructive/20 aria-invalid:border-destructive transition-[color,box-shadow] overflow-hidden",
  {
    variants: {
      variant: {
        solid: "border-transparent",
        outline: "border",
        ghost: "border",
      },
      color: {
        white: "",
        neutral: "",
        indigo: "",
        blue: "",
        red: "",
        pink: "",
        orange: "",
        yellow: "",
        green: "",
        teal: "",
        purple: "",
      },
      size: {
        sm: "text-xs rounded-full px-2 py-0.5 h-6",
        md: "text-sm rounded-full px-3 py-1 h-7",
      },
    },
    compoundVariants: [
      {
        variant: "solid",
        color: "white",
        className: "bg-neutral-100 text-neutral-800"
      },
      {
        variant: "solid",
        color: "neutral",
        className: "bg-neutral-200/90 text-neutral-800"
      },
      {
        variant: "solid",
        color: "indigo",
        className: "bg-indigo-200 text-indigo-800"
      },
      {
        variant: "solid",
        color: "blue",
        className: "bg-blue-200 text-blue-800"
      },
      {
        variant: "solid",
        color: "red",
        className: "bg-red-200 text-red-800"
      },
      {
        variant: "solid",
        color: "pink",
        className: "bg-pink-200 text-pink-800"
      },
      {
        variant: "solid",
        color: "orange",
        className: "bg-orange-200 text-orange-800"
      },
      {
        variant: "solid",
        color: "yellow",
        className: "bg-amber-200 text-amber-800"
      },
      {
        variant: "solid",
        color: "green",
        className: "bg-green-200 text-green-800"
      },
      {
        variant: "solid",
        color: "teal",
        className: "bg-teal-200 text-teal-800"
      },
      {
        variant: "solid",
        color: "purple",
        className: "bg-purple-200 text-purple-800"
      },
      {
        variant: "outline",
        color: "white",
        className: "border-neutral-300 bg-neutral-50/90 text-neutral-800"
      },
      {
        variant: "outline",
        color: "neutral",
        className: "border-neutral-300 bg-neutral-50 text-neutral-800"
      },
      {
        variant: "outline",
        color: "indigo",
        className: "border-indigo-300 bg-indigo-50 text-indigo-800"
      },
      {
        variant: "outline",
        color: "blue",
        className: "border-blue-300 bg-blue-50 text-blue-800"
      },
      {
        variant: "outline",
        color: "red",
        className: "border-red-300 bg-red-50 text-red-800"
      },
      {
        variant: "outline",
        color: "pink",
        className: "border-pink-300 bg-pink-50 text-pink-800"
      },
      {
        variant: "outline",
        color: "orange",
        className: "border-orange-300 bg-orange-50 text-orange-800"
      },
      {
        variant: "outline",
        color: "yellow",
        className: "border-amber-300 bg-amber-50 text-amber-800"
      },
      {
        variant: "outline",
        color: "green",
        className: "border-green-300 bg-green-50 text-green-800"
      },
      {
        variant: "outline",
        color: "teal",
        className: "border-teal-300 bg-teal-50 text-teal-800"
      },
      {
        variant: "outline",
        color: "purple",
        className: "border-purple-300 bg-purple-50 text-purple-800"
      },
      {
        variant: "ghost",
        color: "white",
        className: "border-neutral-100 bg-transparent text-neutral-700"
      },
      {
        variant: "ghost",
        color: "neutral",
        className: "border-neutral-300 bg-transparent text-neutral-800"
      },
      {
        variant: "ghost",
        color: "indigo",
        className: "border-indigo-300 bg-transparent text-indigo-800"
      },
      {
        variant: "ghost",
        color: "blue",
        className: "border-blue-300 bg-transparent text-blue-800"
      },
      {
        variant: "ghost",
        color: "red",
        className: "border-red-300 bg-transparent text-red-800"
      },
      {
        variant: "ghost",
        color: "pink",
        className: "border-pink-300 bg-transparent text-pink-800"
      },
      {
        variant: "ghost",
        color: "orange",
        className: "border-orange-300 bg-transparent text-orange-800"
      },
      {
        variant: "ghost",
        color: "yellow",
        className: "border-amber-300 bg-transparent text-amber-800"
      },
      {
        variant: "ghost",
        color: "green",
        className: "border-green-300 bg-transparent text-green-800"
      },
      {
        variant: "ghost",
        color: "teal",
        className: "border-teal-300 bg-transparent text-teal-800"
      },
      {
        variant: "ghost",
        color: "purple",
        className: "border-purple-300 bg-transparent text-purple-800"
      }
    ],
    defaultVariants: {
      variant: "solid",
      color: "neutral",
      size: "sm",
    },
  }
);

export interface BadgeProps
  extends Omit<React.HTMLAttributes<HTMLElement>, "color">,
    VariantProps<typeof badgeVariants> {
  asChild?: boolean;
}

function Badge({
  className,
  variant,
  color,
  size = "sm",
  asChild = false,
  ...props
}: BadgeProps) {
  const Comp = asChild ? Slot : "span";

  const validSize = size === "sm" || size === "md" ? size : "sm";

  return (
    <BadgeContext.Provider value={{ size: validSize }}>
      <Comp
        data-slot="badge"
        className={cn(
          badgeVariants({
            variant,
            color,
            size: validSize,
          }),
          className
        )}
        {...props}
      />
    </BadgeContext.Provider>
  );
}

const BadgeIcon = ({
  children,
  className,
  ...props
}: React.HTMLAttributes<HTMLSpanElement> & {
  children: React.ReactElement<React.SVGProps<SVGSVGElement>>;
}) => {
  const { size } = useContext(BadgeContext);
  const sizeClasses = size === "sm" ? "w-3 h-3" : "!w-4 !h-4";

  return (
    <span
      className={cn(
        "inline-block",
        sizeClasses,
        className
      )}
      {...props}
    >
      {React.isValidElement(children) &&
        React.cloneElement(children, {
          className: cn(sizeClasses, children.props.className),
        })}
    </span>
  );
};

const BadgeDot = ({
  className,
  color = "neutral",
  ...props
}: React.HTMLAttributes<HTMLSpanElement> & {
  color?: string;
}) => {
  const dotColor = 
    color === "neutral" ? "bg-neutral-700" :
    color === "blue" ? "bg-blue-700" :
    color === "indigo" ? "bg-indigo-800" :
    color === "red" ? "bg-red-700" :
    color === "pink" ? "bg-pink-700" :
    color === "orange" ? "bg-orange-700" :
    color === "yellow" ? "bg-amber-700" :
    color === "green" ? "bg-green-700" :
    color === "teal" ? "bg-teal-700" :
    color === "purple" ? "bg-purple-700":
    "bg-neutral-700";
  return (
    <span
      className={cn(
        "inline-block rounded-full w-1.5 h-1.5",
        dotColor,
        className
      )}
      {...props}
    />
  );
};

const BadgeAvatar = ({
  src,
  alt = "Avatar",
  size = "sm",
  className,
  ...props
}: React.ImgHTMLAttributes<HTMLImageElement> & {
  size?: "sm" | "md";
}) => {
  return (
    <span className="flex">
      <img 
        src={src}
        alt={alt}
        className={cn(
          "rounded-full object-cover",
          size === "sm" ? "w-4 h-4" : "w-5 h-5",
          className
        )}
        {...props}
      />
    </span>
  );
};

const BadgeClose = ({
  size = "sm",
  onClick,
  className,
  ...props
}: React.ButtonHTMLAttributes<HTMLButtonElement> & {
  size?: "sm" | "md";
}) => {
  return (
    <button 
      onClick={(e) => {
        e.stopPropagation();
        onClick?.(e);
      }}
      className={cn(
        "rounded-full hover:bg-neutral-200 flex items-center justify-center w-4 h-4",
        className
      )}
      {...props}
    >
      <X className={"w-4 h-4"} />
    </button>
  );
};

Badge.Icon = BadgeIcon;
Badge.Dot = BadgeDot;
Badge.Avatar = BadgeAvatar;
Badge.Close = BadgeClose;

export { Badge, badgeVariants };

import * as React from "react";
import { Slot } from "@radix-ui/react-slot";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-lg font-medium transition-all disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-auto shrink-0 [&_svg]:shrink-0 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-ring/90 aria-invalid:ring-destructive/20",

  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground hover:bg-primary-hover",
        secondaryBrand: "bg-indigo-50 text-primary hover:bg-indigo-100",
        secondary: "bg-secondary text-secondary-foreground hover:bg-gray-300",
        outlineBrand: "border border-indigo-300 text-primary hover:bg-indigo-100",
        outline: "border border-neutral-600/20 bg-transparent text-secondary-foreground hover:bg-gray-200/50 ",
        destructive: "bg-destructive text-white hover:bg-red-800 focus-visible:ring-destructive/60",
        destructiveOutline: "border border-red-300 text-destructive hover:bg-red-50 focus-visible:ring-destructive/60",
        success: "bg-success text-white hover:bg-green-800 focus-visible:ring-success/60",
        successOutline: "border border-green-300 text-success hover:bg-green-50 focus-visible:ring-success/60",
        ghost: " text-secondary-foreground hover:bg-accent hover:bg-gray-200/50 ",
        link: "text-primary underline-offset-4 hover:underline p-0 h-auto",
      },
      size: {
        md: "h-10 px-4 py-2 [&_svg:not([class*='size-'])]:size-5",
        sm: "h-8 px-3 py-1.5 text-xs gap-1.5 [&_svg:not([class*='size-'])]:size-4",
        lg: "h-12 px-5 py-2.5 text-base [&_svg:not([class*='size-'])]:size-5",
        "icon-sm": "size-9 p-0 [&_svg:not([class*='size-'])]:size-4",
        "icon-md": "size-10 p-0 [&_svg:not([class*='size-'])]:size-5",
        "icon-lg": "size-12 p-0 [&_svg:not([class*='size-'])]:size-5",
      },
      iconPosition: {
        left: "flex-row",
        right: "flex-row",
      },
      fullWidth: {
        true: "w-full",
      },
      hasStartIcon: {
        true: "",
      },
      hasEndIcon: {
        true: "",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "md",
      iconPosition: "left",
      fullWidth: false,
      hasStartIcon: false,
      hasEndIcon: false,
    },
    compoundVariants: [
      {
        iconPosition: "left",
        size: "sm",
        class: "gap-1",
      },
      {
        iconPosition: "right",
        size: "sm",
        class: "gap-1",
      },
      {
        iconPosition: "left",
        size: "md",
        class: "gap-1",
      },
      {
        iconPosition: "right",
        size: "md",
        class: "gap-1",
      },
      {
        iconPosition: "left",
        size: "lg",
        class: "gap-1.5",
      },
      {
        iconPosition: "right",
        size: "lg",
        class: "gap-1.5",
      },
      {
        size: "sm",
        hasStartIcon: true,
        class: "pl-2.5 pr-3",
      },
      {
        size: "sm",
        hasEndIcon: true,
        class: "pl-3 pr-2.5",
      },
      {
        size: "md",
        hasStartIcon: true,
        class: "pl-3.5 pr-4",
      },
      {
        size: "md",
        hasEndIcon: true,
        class: "pl-4 pr-3.5",
      },
      {
        size: "lg",
        hasStartIcon: true,
        class: "pl-4 pr-5",
      },
      {
        size: "lg",
        hasEndIcon: true,
        class: "pl-5 pr-4",
      },
      {
        hasStartIcon: true,
        hasEndIcon: true,
        size: "sm",
        class: "pl-2.5 pr-2.5",
      },
      {
        hasStartIcon: true,
        hasEndIcon: true,
        size: "md",
        class: "pl-3.5 pr-3.5",
      },
      {
        hasStartIcon: true,
        hasEndIcon: true,
        size: "lg",
        class: "pl-4 pr-4",
      },
      {
        variant: "link",
        hasStartIcon: true,
        class: "gap-1.5 p-0 h-auto",
      },
      {
        variant: "link",
        hasEndIcon: true,
        class: "gap-1.5 p-0 h-auto",
      },
    ],
  }
);

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean;
  startIcon?: React.ReactNode;
  endIcon?: React.ReactNode;
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  (
    {
      className,
      variant,
      size,
      fullWidth,
      asChild = false,
      startIcon,
      endIcon,
      children,
      iconPosition: iconPositionProp,
      ...props
    },
    ref
  ) => {
    const Comp = asChild ? Slot : "button";

    const iconPosition = iconPositionProp || (endIcon && !startIcon ? "right" : "left");

    const hasStartIcon = Boolean(startIcon);
    const hasEndIcon = Boolean(endIcon);
    const isIconOnly = !children && (hasStartIcon || hasEndIcon);
    const isLink = variant === "link";

    let effectiveSize = size;
    if (isIconOnly && !String(size).startsWith("icon-") && !isLink) {
      if (size === "sm") effectiveSize = "icon-sm";
      else if (size === "lg") effectiveSize = "icon-lg";
      else effectiveSize = "icon-md";
    }

    return (
      <Comp
        className={cn(
          buttonVariants({
            variant,
            size: effectiveSize,
            fullWidth,
            iconPosition,
            hasStartIcon: !isIconOnly && hasStartIcon,
            hasEndIcon: !isIconOnly && hasEndIcon,
            className,
          })
        )}
        ref={ref}
        {...props}
      >
        {isIconOnly ? (
          <span className="flex items-center justify-center">
            {startIcon || endIcon}
          </span>
        ) : (
          <>
            {iconPosition === "left" && hasStartIcon && (
              <span className="shrink-0">{startIcon}</span>
            )}
            {children}
            {iconPosition === "right" && hasEndIcon && (
              <span className="shrink-0">{endIcon}</span>
            )}
          </>
        )}
      </Comp>
    );
  }
);

Button.displayName = "Button";

export { Button, buttonVariants };

"use client";

import { Toaster as Sonner, ToasterProps } from "sonner";

import { Info, LoaderCircle, CircleCheck, TriangleAlert, OctagonAlert } from "lucide-react";

const Toaster = ({ ...props }: ToasterProps) => {
  return (
    <Sonner
      className="toaster group"
      toastOptions={{
        classNames: {
          toast: 'bg-popover text-popover-foreground shadow-lg  rounded-lg border p-4',
          title: 'text-color-fg text-sm/5 font-semibold',
          description: 'text-color-fg/80 text-sm/5 font-medium',
          actionButton:
              "!h-8 !whitespace-nowrap !bg-primary ! rounded-lg !px-3 !font-medium !text-sm hover:bg-primary/90",
          cancelButton:
              "!h-8 !whitespace-nowrap ! rounded-lg !px-3 !font-medium !text-sm hover:bg-color-fg/10",
          loader: 'bg-color-fg/10',
          closeButton: 'bg-color-fg/10 hover:bg-color-fg/20',
        },
      }}
      icons={{
        success: <CircleCheck size={20} />, 
        info: <Info size={20} />, 
        warning: <TriangleAlert size={20} />, 
        error: <OctagonAlert size={20} />, 
        loading: <LoaderCircle size={20} />,
      }}
      style={{
        "--normal-bg": "var(--color-popover)",
        "--normal-text": "var(--color-popover-foreground)",
        "--normal-border": "var(--border-input)",
      } as React.CSSProperties}
      {...props}
    />
  );
};

export { Toaster };

import React from "react";
import { LucideIcon } from "lucide-react"; // Import LucideIcon type
import clsx from "clsx"; // Utility for conditional class names

interface FeaturedIconProps {
  color: string;
  icon: LucideIcon;
}

const FeaturedIcon: React.FC<FeaturedIconProps> = ({ color, icon: Icon }) => {
  // Derive the outline color based on the provided color
  const auraColor = color.replace("-600", "-200");

  return (
    <>
    <div
      className={clsx(
        "inline-flex size-6 items-center justify-center rounded-full",
        `text-${color}`,
        `bg-${auraColor}`
      )}
      style={{
        boxShadow: `0 0 0 8px var(--color-${auraColor})`,
      }}
    >
      <Icon size={16} className={`size-6 h-5 w-5 text-${color}`} />
    </div>
</>
  );
};

export default FeaturedIcon;

"use client";

import * as React from "react";
import {
  ColumnDef,
  ColumnFiltersState,
  SortingState,
  VisibilityState,
  flexRender,
  getCoreRowModel,
  getFilteredRowModel,
  getPaginationRowModel,
  getSortedRowModel,
  useReactTable,
} from "@tanstack/react-table";
import { ArrowPathIcon, EllipsisHorizontalIcon, PlusIcon, PrinterIcon } from '@heroicons/react/16/solid';
import { ArrowUpDown, ChevronDown, MoreHorizontal, PlusCircle } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Input } from "@/components/ui/input";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { toast } from "sonner";

interface DataTableProps<TData, TValue> {
  columns: ColumnDef<TData, TValue>[]
  data: TData[]
}

export function DataTable<TData, TValue>({
  columns,
  data,
}: DataTableProps<TData, TValue>) {
  const [sorting, setSorting] = React.useState<SortingState>([]);
  const [columnFilters, setColumnFilters] = React.useState<ColumnFiltersState>(
    []
  );
  const [columnVisibility, setColumnVisibility] =
    React.useState<VisibilityState>({});
  const [rowSelection, setRowSelection] = React.useState({});

  const table = useReactTable({
    data,
    columns,
    onSortingChange: setSorting,
    onColumnFiltersChange: setColumnFilters,
    getCoreRowModel: getCoreRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    onColumnVisibilityChange: setColumnVisibility,
    onRowSelectionChange: setRowSelection,
    state: {
      sorting,
      columnFilters,
      columnVisibility,
      rowSelection,
    },
  });

  return (
    <div className="w-full">
      <div className="flex items-center pt-1 pb-4 gap-2">
        <Button
          variant="default"
          size="sm"
          startIcon={<PlusIcon />}
          onClick={() => toast.success("Task has been created")}
        >
          Create Task
        </Button>
        <Input
          placeholder="Search task..."
          value={(table.getColumn("taskName")?.getFilterValue() as string) ?? ""}
          onChange={(event) =>
            table.getColumn("taskName")?.setFilterValue(event.target.value)
          }
          className="ml-auto max-w-xs"
        />
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button variant="outline" size="sm" className="">
              Columns <ChevronDown />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end">
            {table
              .getAllColumns()
              .filter((column) => column.getCanHide())
              .map((column) => {
                return (
                  <DropdownMenuCheckboxItem
                    key={column.id}
                    className="capitalize"
                    checked={column.getIsVisible()}
                    onCheckedChange={(value) =>
                      column.toggleVisibility(!!value)
                    }
                  >
                    {column.id}
                  </DropdownMenuCheckboxItem>
                );
              })}
          </DropdownMenuContent>
        </DropdownMenu>
        <div className="gap-1">
          <Button
            variant="ghost"
            size="icon-sm"
            startIcon={<ArrowPathIcon />}
          />
          <Button
            variant="ghost"
            size="icon-sm"
            startIcon={<PrinterIcon />}
          />

          <Button
            variant="ghost"
            size="icon-sm"
            startIcon={<EllipsisHorizontalIcon />}
          />
        </div>
      </div>
      <div className="rounded-md border">
        <Table>
          <TableHeader>
            {table.getHeaderGroups().map((headerGroup) => (
              <TableRow key={headerGroup.id}>
                {headerGroup.headers.map((header) => {
                  return (
                    <TableHead key={header.id}>
                      {header.isPlaceholder
                        ? null
                        : flexRender(
                            header.column.columnDef.header,
                            header.getContext()
                          )}
                    </TableHead>
                  );
                })}
              </TableRow>
            ))}
          </TableHeader>
          <TableBody>
            {table.getRowModel().rows?.length ? (
              table.getRowModel().rows.map((row) => (
                <TableRow
                  key={row.id}
                  data-state={row.getIsSelected() && "selected"}
                >
                  {row.getVisibleCells().map((cell) => (
                    <TableCell key={cell.id}>
                      {flexRender(
                        cell.column.columnDef.cell,
                        cell.getContext()
                      )}
                    </TableCell>
                  ))}
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell
                  colSpan={columns.length}
                  className="h-24 text-center"
                >
                  No results.
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>
      <div className="flex items-center justify-end space-x-2 py-4">
        <div className="flex-1 text-sm text-muted-foreground">
          {table.getFilteredSelectedRowModel().rows.length} of{" "}
          {table.getFilteredRowModel().rows.length} row(s) selected.
        </div>
        <div className="space-x-2">
          <Button
            variant="outline"
            size="sm"
            onClick={() => table.previousPage()}
            disabled={!table.getCanPreviousPage()}
          >
            Previous
          </Button>
          <Button
            variant="outline"
            size="sm"
            onClick={() => table.nextPage()}
            disabled={!table.getCanNextPage()}
          >
            Next
          </Button>
        </div>
      </div>
    </div>
  );
}

"use client";

import { ColumnDef } from "@tanstack/react-table";
import { Badge } from "@/components/ui/badge";
